// haslightbox
(function () {
    let lightboxItens = $('[data-lightbox]');

    if (lightboxItens.length > 0) {
        let css = document.createElement('link');

        css.rel = 'stylesheet';
        css.href = 'https://cdnjs.cloudflare.com/ajax/libs/lightbox2/2.11.1/css/lightbox.min.css';
        css.type = 'text/css';

        document.head.appendChild(css);

        let js = document.createElement('script');

        js.src = 'https://cdnjs.cloudflare.com/ajax/libs/lightbox2/2.11.1/js/lightbox.min.js';

        document.body.appendChild(js);
    }

})();



document.addEventListener('DOMContentLoaded', function () {
	// Obt√©m todas as divs com a classe "linha"
	var linhas = document.querySelectorAll('.linha');

	// Adiciona um listener de clique a cada div com a classe "linha"
	linhas.forEach(function (linha) {
		linha.addEventListener('click', function () {
			// Adiciona ou remove a classe "active" ao clicar na div
			this.classList.toggle('active');
		});
	});

	// Adiciona um listener de clique ao documento para remover a classe "active" ao clicar fora da div
	document.addEventListener('click', function (event) {
		if (!event.target.closest('.linha')) {
			// Remove a classe "active" de todas as divs com a classe "linha"
			linhas.forEach(function (linha) {
				linha.classList.remove('active');
			});
		}
	});
});
